function s(n){if(!(n instanceof Date)||isNaN(n.getTime()))return"";let t=n.getDate().toString().padStart(2,"0"),r=(n.getMonth()+1).toString().padStart(2,"0"),e=n.getFullYear().toString();return`${t}/${r}/${e}`}function g(n){let[t,r,e]=n.split("/").map(o=>parseInt(o,10));if(!t||!r||!e)throw new Error("Invalid French date format. Expected DD/MM/YYYY.");return new Date(e,r-1,t)}function c(n){let t=new Date(n),r=t.getDate().toString().padStart(2,"0"),e=(t.getMonth()+1).toString().padStart(2,"0"),o=t.getFullYear().toString(),a=t.getHours().toString().padStart(2,"0"),i=t.getMinutes().toString().padStart(2,"0");return`${r}/${e}/${o} ${a}:${i}`}export{s as a,g as b,c};
